      var hiddenTopItems = $.data(submenu[0], "hiddenTopItems"),
      hiddenBottomItems = $.data(submenu[0], "hiddenBottomItems"),
      visibleSubMenus = $.data(submenu[0], "visibleSubMenus"),
      upButton = $.data(submenu[0], "upButton"),
      downButton = $.data(submenu[0], "downButton"),
      bottomElem = null,
      topElem = null;

